SELECT tmp.id, tmp.NAME,tmp.is_subscribe, tmp.picuri, tmp.inventorynumber,is_specification,origin,tmp.del_flag,tmp.sell_time,tmp.sell_end_time,tmp.sell_status,tmp.price,tmp.company_id,catalog_id_1 from product tmp left join company c on tmp.company_id=c.company_id right join user_cart ucs on ucs.prod_id=tmp.id WHERE tmp.company_id = #{companyId} and c.isdel=1 and ucs.user_id=#{user_id} and tmp.id in
#{item}
and v_state=1 and tmp.del_flag=0

mapper.update:
update product
id = #{id}, name = #{name}, price = #{price}, descn = #{descn}, picuri = #{picuri}, status = #{status}, weight = #{weight}, modified = #{modified}, specid = #{specid}, created = #{created}, paycode = #{paycode}, inventorynumber = #{inventorynumber}, warningnumber = #{warningnumber}, company_id = #{companyId}, unit = #{unit}, istop = #{istop}, remark = #{remark}, sn = #{sn}, market_price = #{marketPrice}, del_flag = #{delFlag}, freight = #{freight}, inventory_calculation = #{inventoryCalculation}, sell_time = #{sellTime}, sell_end_time = #{sellEndTime}, is_specification = #{isSpecification}, sell_status = #{sellStatus}, v_state = #{vState}, v_content = #{vContent}, v_time = #{vTime}, is_subscribe = #{isSubscribe}, catalog_id_1 = #{catalogId1}, catalog_id_2 = #{catalogId2}, type = #{type}, score = #{score}+score, prod_tags_id = #{prodTagsId}, prod_basic_id = #{prodBasicsId}, origin = #{origin}, note = #{note},
where id = #{id}

update product set
inventorynumber = inventorynumber-#{inventorynumber} where id = #{id}
and (inventorynumber-#{inventorynumber})>0 and sell_status =1

mapper.select:
